import React, { useState } from "react";
import { InputText } from 'primereact/inputtext';
import { Password } from 'primereact/password';
import { Button } from 'primereact/button';
import { Card } from 'primereact/card';
import './Login.css';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({
    usuario: '',
    contraseña: ''
  });
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    
    // Validación temporal - luego conectaremos con el backend
    if(credentials.usuario === 'administrativa' && credentials.contraseña === 'admin123456') {
      onLogin(true);
    } else {
      setError('Usuario o contraseña incorrectos');
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h2>Bienvenidos a la Tienda Genérica</h2>
        </div>
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="usuario">Usuario</label>
            <InputText
              id="usuario"
              value={credentials.usuario}
              onChange={(e) => setCredentials({...credentials, usuario: e.target.value})}
              className="form-input"
              placeholder="Ingrese su usuario"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="contraseña">Contraseña</label>
            <Password
              id="contraseña"
              value={credentials.contraseña}
              onChange={(e) => setCredentials({...credentials, contraseña: e.target.value})}
              feedback={false}
              toggleMask
              className="form-input"
              placeholder="Ingrese su contraseña"
              inputClassName="form-input"
            />
          </div>
          
          {error && <div className="error-message">{error}</div>}
          
          <Button 
            label="Ingresar" 
            type="submit" 
            className="login-button" 
          />
          
          <div className="login-footer">
            Usuario: administrativa | Contraseña: admin123456
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
