import React, { useState } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { Password } from 'primereact/password';
import './UsersCRUD.css';

const UsersCRUD = () => {
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState({});
  const [userDialog, setUserDialog] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  const openNew = () => {
    setUser({});
    setSubmitted(false);
    setUserDialog(true);
  };

  const hideDialog = () => {
    setSubmitted(false);
    setUserDialog(false);
  };

  const saveUser = () => {
    setSubmitted(true);

    if (user.cedula && user.nombre && user.correo && user.usuario && user.contraseña) {
      let _users = [...users];
      if (user.id) {
        const index = _users.findIndex(u => u.id === user.id);
        _users[index] = user;
      } else {
        _users.push({...user, id: Math.random()});
      }
      setUsers(_users);
      setUserDialog(false);
      setUser({});
    }
  };

  const editUser = (user) => {
    setUser({...user});
    setUserDialog(true);
  };

  const deleteUser = (user) => {
    let _users = users.filter(u => u.id !== user.id);
    setUsers(_users);
  };

  const userDialogFooter = (
    <React.Fragment>
      <Button label="Cancelar" icon="pi pi-times" className="p-button-text" onClick={hideDialog} />
      <Button label="Guardar" icon="pi pi-check" onClick={saveUser} />
    </React.Fragment>
  );

  return (
    <div className="crud-container">
      <div className="card">
        <div className="table-header">
          <h2>Gestión de Usuarios</h2>
          <Button label="Nuevo" icon="pi pi-plus" className="p-button-success" onClick={openNew} />
        </div>

        <DataTable value={users} responsiveLayout="scroll">
          <Column field="cedula" header="Cédula"></Column>
          <Column field="nombre" header="Nombre Completo"></Column>
          <Column field="correo" header="Correo Electrónico"></Column>
          <Column field="usuario" header="Usuario"></Column>
          <Column body={(rowData) => (
            <>
              <Button icon="pi pi-pencil" className="p-button-rounded p-button-success p-mr-2" onClick={() => editUser(rowData)} />
              <Button icon="pi pi-trash" className="p-button-rounded p-button-danger" onClick={() => deleteUser(rowData)} />
            </>
          )}></Column>
        </DataTable>
      </div>

      <Dialog visible={userDialog} style={{ width: '450px' }} header="Detalles de Usuario" modal className="p-fluid" footer={userDialogFooter} onHide={hideDialog}>
        <div className="p-field">
          <label htmlFor="cedula">Cédula</label>
          <InputText id="cedula" value={user.cedula || ''} onChange={(e) => setUser({...user, cedula: e.target.value})} required autoFocus className={submitted && !user.cedula ? 'p-invalid' : ''} />
          {submitted && !user.cedula && <small className="p-error">Cédula es requerida.</small>}
        </div>
        <div className="p-field">
          <label htmlFor="nombre">Nombre Completo</label>
          <InputText id="nombre" value={user.nombre || ''} onChange={(e) => setUser({...user, nombre: e.target.value})} required className={submitted && !user.nombre ? 'p-invalid' : ''} />
          {submitted && !user.nombre && <small className="p-error">Nombre es requerido.</small>}
        </div>
        <div className="p-field">
          <label htmlFor="correo">Correo Electrónico</label>
          <InputText id="correo" value={user.correo || ''} onChange={(e) => setUser({...user, correo: e.target.value})} required className={submitted && !user.correo ? 'p-invalid' : ''} />
          {submitted && !user.correo && <small className="p-error">Correo es requerido.</small>}
        </div>
        <div className="p-field">
          <label htmlFor="usuario">Usuario</label>
          <InputText id="usuario" value={user.usuario || ''} onChange={(e) => setUser({...user, usuario: e.target.value})} required className={submitted && !user.usuario ? 'p-invalid' : ''} />
          {submitted && !user.usuario && <small className="p-error">Usuario es requerido.</small>}
        </div>
        <div className="p-field">
          <label htmlFor="contraseña">Contraseña</label>
          <Password id="contraseña" value={user.contraseña || ''} onChange={(e) => setUser({...user, contraseña: e.target.value})} required feedback={false} className={submitted && !user.contraseña ? 'p-invalid' : ''} />
          {submitted && !user.contraseña && <small className="p-error">Contraseña es requerida.</small>}
        </div>
      </Dialog>
    </div>
  );
};

export default UsersCRUD;
