import React, { useState } from "react";
import { InputText } from 'primereact/inputtext';
import { Password } from 'primereact/password';
import { Button } from 'primereact/button';
import { Card } from 'primereact/card';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({
    usuario: '',
    contraseña: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validación temporal - luego conectaremos con el backend
    if(credentials.usuario === 'administrativa' && credentials.contraseña === 'admin123456') {
      onLogin(true);
    }
  };

  return (
    <div className="p-d-flex p-jc-center p-ai-center" style={{ height: '100vh' }}>
      <Card title="Bienvenidos a la Tienda Genérica" className="p-col-4">
        <form onSubmit={handleSubmit}>
          <div className="p-field">
            <label htmlFor="usuario">Usuario</label>
            <InputText
              id="usuario"
              value={credentials.usuario}
              onChange={(e) => setCredentials({...credentials, usuario: e.target.value})}
              className="p-mb-2"
            />
          </div>
          <div className="p-field">
            <label htmlFor="contraseña">Contraseña</label>
            <Password
              id="contraseña"
              value={credentials.contraseña}
              onChange={(e) => setCredentials({...credentials, contraseña: e.target.value})}
              feedback={false}
            />
          </div>
          <Button label="Ingresar" type="submit" className="p-mt-2" />
        </form>
      </Card>
    </div>
  );
};

export default Login;
